grant insert on table "public"."anime_vod" to "authenticated";

grant references on table "public"."anime_vod" to "authenticated";

grant select on table "public"."anime_vod" to "authenticated";

grant trigger on table "public"."anime_vod" to "authenticated";

grant truncate on table "public"."anime_vod" to "authenticated";

grant update on table "public"."anime_vod" to "authenticated";

grant delete on table "public"."anime_vod" to "service_role";

grant insert on table "public"."anime_vod" to "service_role";

grant references on table "public"."anime_vod" to "service_role";

grant select on table "public"."anime_vod" to "service_role";

grant trigger on table "public"."anime_vod" to "service_role";

grant truncate on table "public"."anime_vod" to "service_role";

grant update on table "public"."anime_vod" to "service_role";

